(671, 162, '_wp_attachment_metadata', 'a:6:{s:5:\"width\";s:3:\"303\";s:6:\"height\";s:3:\"514\";s:14:\"hwstring_small\";s:22:\"height=\'96\' width=\'56\'\";s:4:\"file\";s:29:\"2014/09/clip_padding_true.png\";s:5:\"sizes\";a:2:{s:9:\"thumbnail\";a:3:{s:4:\"file\";s:29:\"clip_padding_true-150x150.png\";s:5:\"width\";s:3:\"150\";s:6:\"height\";s:3:\"150\";}s:6:\"medium\";a:3:{s:4:\"file\";s:29:\"clip_padding_true-176x300.png\";s:5:\"width\";s:3:\"176\";s:6:\"height\";s:3:\"300\";}}s:10:\"image_meta\";a:10:{s:8:\"aperture\";s:1:\"0\";s:6:\"credit\";s:0:\"\";s:6:\"camera\";s:0:\"\";s:7:\"caption\";s:0:\"\";s:17:\"created_timestamp\";s:1:\"0\";s:9:\"copyright\";s:0:\"\";s:12:\"focal_length\";s:1:\"0\";s:3:\"iso\";s:1:\"0\";s:13:\"shutter_speed\";s:1:\"0\";s:5:\"title\";s:0:\"\";}}'),
